// Combine all input items into a single array in one output item
const results = $input.all().map(item => {
  let content = item.json.content;

  // Remove code fences if present
  content = content.replace(/```json|```/g, '').trim();

  // Parse safely
  let data;
  try {
    data = JSON.parse(content);
  } catch (e) {
    data = {};
  }

  // Extract category and rename id ‚Üí to_id
  return {
    categoryID: data.categoryID || null,
    to_id: data.id || null,
    file_name: data.file_name
  };
});

return [{ json: { results } }];

// n8n Code Node - Place this after your Split Out1 node
// This code runs for ALL items together

// Get all items from the Split Out1 node (documents list)
const splitItems = $('Split Out1').all();

// Get the code node output with results from "Extract IDs" node
const codeResults = $('Extract IDs').first().json.results;

// Create a lookup Map for fast matching
const lookupMap = new Map();
codeResults.forEach(item => {
  lookupMap.set(String(item.to_id), {
    categoryID: item.categoryID,
    file_name: item.file_name
  });
});

// Filter and enrich items that have matching IDs
const matchedItems = splitItems
  .filter(item => lookupMap.has(String(item.json.id)))
  .map(item => ({
    json: {
      ...item.json,
      categoryID: lookupMap.get(String(item.json.id)).categoryID,
      file_name: lookupMap.get(String(item.json.id)).file_name
    }
  }));

// Return only matched items
return matchedItems;

// Combine all input items into a single array in one output item
const results = $input.all().map(item => {
  // Get content from the CURRENT item, not the first one
  let content = item.json.content.parts[0].text;

  // Remove code fences if present
  content = content.replace(/```json|```/g, '').trim();

  // Parse safely
  let data;
  try {
    data = JSON.parse(content);
  } catch (e) {
    data = {};
  }

  // Extract category and rename id ‚Üí to_id
  return {
    categoryID: data.categoryID || null,
    to_id: data.id || null,
    file_name: data.file_name,
    ext: data.extracted_text
  };
});

return [{ json: { results } }];

// Get all items from the Split Out2 node (documents list)
const splitItems = $('Split Out2').all();

// Get results from the "Extract IDs1" node
const extractNode = $('Extract IDs1').first().json;
const codeResults = Array.isArray(extractNode.results) ? extractNode.results : [];

// Create a lookup Map for quick ID matching
const lookupMap = new Map();
codeResults.forEach(item => {
  lookupMap.set(String(item.to_id), {
    categoryID: item.categoryID,
    file_name: item.file_name
  });
});

// Filter and enrich matching items
const matchedItems = splitItems
  .filter(item => lookupMap.has(String(item.json.to_id || item.json.id)))
  .map(item => {
    const match = lookupMap.get(String(item.json.to_id || item.json.id));
    return {
      json: {
        ...item.json,
        categoryID: match.categoryID,
        file_name: match.file_name
      }
    };
  });

// Return only matched items
return matchedItems.length ? matchedItems : [{ json: { message: "No matching IDs found" } }];

// Cleans OCR text safely from "Split Out3" output
// Prevents missing value errors

return $input.all().map((item, index) => {
  const json = item.json ?? {};

  // Verify that 'ext' exists and is a string
  let text = '';
  if (json.hasOwnProperty('ext') && typeof json.ext === 'string') {
    text = json.ext;
  } else {
    console.log(`‚ö†Ô∏è Item ${index}: 'ext' field missing or invalid.`);
  }

  // Proceed only if there's something to clean
  if (text.trim() !== '') {
    // Remove Start/End OCR markers (any page number)
    text = text.replace(/==\s*(Start|End)\s+of\s+OCR\s+for\s+page\s*\d+\s*==/gi, '');

    // Remove newlines and extra spaces
    text = text.replace(/\r?\n+/g, ' ')
               .replace(/\s{2,}/g, ' ')
               .trim();
  }

  // Return updated item safely
  return {
    json: {
      ...json,
      ext: text || ''
    }
  };
});

// Get the dynamic ID from the Form Trigger node
const caseId = $input.first().json.CaseID; // Change 'case_id' to your field name

const baseUrl = `https://casepeerai.onrender.com/api/v1/case/case-documents/${caseId}/?page=`;
let page = 1;
let results = [];

while (true) {
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: `${baseUrl}${page}`,
    json: true,
  });

  if (!response.results?.length) break;
  results.push(...response.results);
  if (response.results.length < response.page_size) break;
  page++;
}

return results.map(r => ({ json: r }));

try {
  // Get the CaseID from the Form Trigger node
  const caseId = $('When Executed by Another Workflow').first().json.CaseID;

  if (!caseId) {
    return [{ json: { error: 'No CaseID found in input' } }];
  }

  const baseUrl = `https://fissureless-arline-creepily.ngrok-free.dev/api/v1/case/case-documents/${caseId}/?page=`;
  let page = 1;
  let results = [];

  while (true) {
    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: `${baseUrl}${page}`,
      json: true,
    });

    if (!response.results?.length) break;
    results.push(...response.results);
    if (response.results.length < response.page_size) break;
    page++;

    // Safety limit to prevent infinite loops
    if (page > 100) break;
  }

  // Return empty array if no results found
  if (results.length === 0) {
    return [{ json: { message: 'No documents found', caseId: caseId, total: 0 } }];
  }

  return results.map(r => ({ json: r }));

} catch (error) {
  return [{ json: { error: error.message, caseId: caseId } }];
}

// Get the dynamic ID from the Form Trigger node
const caseId = $('When Executed by Another Workflow').first().json.CaseID; // Change 'case_id' to your field name

const baseUrl = `https://casepeerai.onrender.com/api/v1/case/case-documents/${caseId}/?page=`;
let page = 1;
let results = [];

while (true) {
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: `${baseUrl}${page}`,
    json: true,
  });

  if (!response.results?.length) break;
  results.push(...response.results);
  if (response.results.length < response.page_size) break;
  page++;
}

return results.map(r => ({ json: r }));

// Parse each item's content (with or without code fences)
return $input.all().map(item => {
  let text = item.json.content;

  // Remove code fences like ```json or ```
  text = text.replace(/```json|```/gi, '').trim();

  try {
    // Attempt to parse the cleaned JSON
    const data = JSON.parse(text);
    return { json: data };
  } catch (error) {
    // If parsing fails, return an error message instead of breaking the workflow
    return { json: { error: error.message, raw: text } };
  }
});

const html = items[0].json.response || items[0].json.body;

// Extract patient name
const titleMatch = html.match(/<title>\s*([^,]+),\s+([^-]+)\s+-\s+Home/);
const lastName = titleMatch ? titleMatch[1].trim() : null;
const firstName = titleMatch ? titleMatch[2].trim() : null;

// Extract case status
const statusMatch = html.match(/<option value="(\d+)" selected>([^<]+)<\/option>/);
const caseStatusId = statusMatch ? statusMatch[1] : null;
const caseStatus = statusMatch ? statusMatch[2].trim() : null;

return items.map(item => ({
  json: {
    patientFirstName: firstName,
    patientLastName: lastName,
    patientFullName: `${firstName} ${lastName}`,
    caseStatus: caseStatus,
    caseStatusId: caseStatusId
  }
}));

return (() => {
  const items = $input.all(); // Get all items from the previous node

  return items.map(item => {
    try {
      // Extract raw content
      const raw = item.json.content || '';

      // Remove code fences like ```json and ```
      const clean = raw.replace(/```json\n?|\n?```/g, '');

      // Parse JSON safely
      const obj = JSON.parse(clean);

      // Return structured JSON for each item
      return {
        json: {
          categoryID: obj.categoryID || '',
          confidence: obj.confidence || '',
          id: obj.id || '',
          file_name: obj.file_name || '',
          content: obj.Content || '',
          case_id: obj.case_id || '',
          ocr_success: obj.ocr_success ?? null // use null if missing
        }
      };

    } catch (error) {
      // Handle malformed items gracefully
      return {
        json: {
          error: error.message,
          rawContent: item.json.content || ''
        }
      };
    }
  });
})();

